import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import axios from 'axios';
import Navigation from './components/Navigation';
import Login from './components/Login';
import SignupScreen from './components/Signup';
import VerifyOtp from './components/VerifyOtp';
import Dashboard from './components/Dashboard';
import Trends from './components/Trends';
import Settings from './components/Settings';
import SchemaExplorer from './components/SchemaExplorer';

// Firebase configuration
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY || "your-firebase-api-key",
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN || "your-project.firebaseapp.com",
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID || "your-project-id",
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET || "your-project.appspot.com",
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID || "123456789",
  appId: process.env.REACT_APP_FIREBASE_APP_ID || "1:123456789:web:abcdef123456"
};

// Initialize Firebase (only if not already initialized)
if (!window.firebaseInitialized) {
  import('firebase/app').then(({ initializeApp }) => {
    initializeApp(firebaseConfig);
    window.firebaseInitialized = true;
  });
}

// Set axios base URL for backend
axios.defaults.baseURL = 'http://localhost:5000';

function App() {
  return (
    <Router basename="/server-monitoring-sms">
      {/* Route-aware mobile overlay: shows on mobile/tablet except for exempt routes */}
      <MobileOverlay />
      <div className="min-h-screen bg-gray-100">
        {/* Mobile/Tablet overlay - visible only via CSS on small viewports */}
        <div className="mobile-only-overlay" role="dialog" aria-hidden="false">
          <h1>This app is best viewed on a PC / Laptop</h1>
          <p>
            This is a system-based monitoring application that displays device and server
            status. For accurate and complete information, please open this site on a
            desktop or laptop computer.
          </p>
          <div className="hint">(No changes made to application logic — UI only)</div>
        </div>
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/signup" element={<SignupScreen />} />
          <Route path="/verify-otp" element={<VerifyOtp />} />
          <Route path="/dashboard" element={
            <>
              <Navigation />
              <Dashboard />
            </>
          } />
          <Route path="/trends" element={
            <>
              <Navigation />
              <Trends />
            </>
          } />
          <Route path="/settings" element={
            <>
              <Navigation />
              <Settings />
            </>
          } />
          <Route path="/schema-explorer" element={
            <>
              <Navigation />
              <SchemaExplorer />
            </>
          } />
        </Routes>
      </div>
    </Router>
  );
}

// Mobile overlay component must be used inside Router so it can access location
function MobileOverlay() {
  const { pathname } = useLocation();
  // don't show overlay on these routes (allow Dashboard and Trends to display on mobile)
  const exempt = ['/dashboard', '/trends'];
  if (exempt.includes(pathname)) return null;

  return (
    <div className="mobile-only-overlay" role="dialog" aria-hidden="false">
      <h1>This app is best viewed on a PC / Laptop</h1>
      <p>
        This is a system-based monitoring application that displays device and server
        status. For accurate and complete information, please open this site on a
        desktop or laptop computer.
      </p>
      <div className="hint">(No changes made to application logic — UI only)</div>
    </div>
  );
}

export default App;
